      out.write("                        let timeConfirmed = false;\n");
      out.write("\n");
      out.write("                        function loadMovies() {\n");
      out.write("                            fetch('movies?action=nowShowing&format=json')\n");
      out.write("                                .then(response => response.json())\n");
      out.write("                                .then(movies => {\n");
      out.write("                                    allMovies = movies.map(movie => {\n");
      out.write("                                        let status = movie.status;\n");
      out.write("                                        if (status === false || status === 'false' || status === null || status === undefined) {\n");
      out.write("                                            status = '';\n");
      out.write("                                        }\n");
      out.write("                                        return {\n");
      out.write("                                            id: movie.id,\n");
      out.write("                                            title: movie.title || '',\n");
      out.write("                                            status: status || '',\n");
      out.write("                                            duration: movie.duration || 0\n");
      out.write("                                        };\n");
      out.write("                                    });\n");
      out.write("                                    searchMovies();\n");
      out.write("                                })\n");
      out.write("                                .catch(error => {\n");
      out.write("                                    console.error('Không thể tải danh sách phim:', error);\n");
      out.write("                                });\n");
      out.write("                        }\n");
      out.write("\n");
      out.write("                        function searchMovies() {\n");
      out.write("                            const searchTerm = document.getElementById('movieSearch').value.toLowerCase();\n");
      out.write("                            const movieList = document.getElementById('movieList');\n");
      out.write("                            movieList.innerHTML = '';\n");
      out.write("\n");
      out.write("                            const filteredMovies = allMovies.filter(movie =>\n");
      out.write("                                movie.title.toLowerCase().includes(searchTerm)\n");
      out.write("                            );\n");
      out.write("\n");
      out.write("                            if (filteredMovies.length === 0) {\n");
      out.write("                                movieList.innerHTML = '<p class=\"text-center text-muted\">Không tìm thấy phim nào</p>';\n");
      out.write("                                return;\n");
      out.write("                            }\n");
      out.write("\n");
      out.write("                            filteredMovies.forEach(movie => {\n");
      out.write("                                const movieItem = document.createElement('div');\n");
      out.write("                                movieItem.className = 'movie-item mb-3 p-3';\n");
      out.write("                                movieItem.style.cssText = 'border: 2px solid transparent; border-radius: 8px; cursor: pointer; transition: all 0.3s;';\n");
      out.write("                                movieItem.setAttribute('data-movie-id', movie.id);\n");
      out.write("                                movieItem.onclick = () => selectMovie(movie.id, movie.title);\n");
      out.write("\n");
      out.write("                                const isSelected = selectedMovieId === movie.id;\n");
      out.write("                                if (isSelected) {\n");
      out.write("                                    movieItem.style.borderColor = 'var(--cinema-gold)';\n");
      out.write("                                    movieItem.style.backgroundColor = 'rgba(255, 193, 7, 0.1)';\n");
      out.write("                                }\n");
      out.write("\n");
      out.write("                                let statusText = '';\n");
      out.write("                                if (movie.status &&\n");
      out.write("                                    movie.status !== false &&\n");
      out.write("                                    movie.status !== 'false' &&\n");
      out.write("                                    movie.status !== 'null' &&\n");
      out.write("                                    movie.status !== 'undefined' &&\n");
      out.write("                                    movie.status !== null &&\n");
      out.write("                                    movie.status !== undefined) {\n");
      out.write("                                    const statusStr = String(movie.status);\n");
      out.write("                                    if (statusStr && statusStr.trim() !== '') {\n");
      out.write("                                        statusText = statusStr;\n");
      out.write("                                    }\n");
      out.write("                                }\n");
      out.write("\n");
      out.write("                                const durationText = movie.duration && movie.duration > 0 ? ' • ' + movie.duration + ' phút' : '';\n");
      out.write("\n");
      out.write("                                const movieTitle = movie.title || '';\n");
      out.write("                                const movieStatusHtml = statusText || durationText ? '<small style=\"color: var(--cinema-text-light);\">' + statusText + durationText + '</small>' : '';\n");
      out.write("                                const checkIconDisplay = isSelected ? 'block' : 'none';\n");
      out.write("\n");
      out.write("                                movieItem.innerHTML =\n");
      out.write("                                    '<div class=\"d-flex justify-content-between align-items-center\">' +\n");
      out.write("                                    '<div>' +\n");
      out.write("                                    '<h5 style=\"color: var(--cinema-gold); margin: 0;\">' + movieTitle + '</h5>' +\n");
      out.write("                                    movieStatusHtml +\n");
      out.write("                                    '</div>' +\n");
      out.write("                                    '<i class=\"fas fa-check-circle\" style=\"display: ' + checkIconDisplay + '; color: var(--cinema-gold);\"></i>' +\n");
      out.write("                                    '</div>';\n");
      out.write("                                movieList.appendChild(movieItem);\n");
      out.write("                            });\n");
      out.write("                        }\n");
      out.write("\n");
      out.write("                        function selectMovie(movieId, movieTitle) {\n");
      out.write("                            selectedMovieId = movieId;\n");
      out.write("                            document.getElementById('selectedMovieId').value = movieId;\n");
      out.write("\n");
      out.write("                            document.querySelectorAll('.movie-item').forEach(item => {\n");
      out.write("                                const id = parseInt(item.getAttribute('data-movie-id'));\n");
      out.write("                                if (id === movieId) {\n");
      out.write("                                    item.style.borderColor = 'var(--cinema-gold)';\n");
      out.write("                                    item.style.backgroundColor = 'rgba(255, 193, 7, 0.1)';\n");
      out.write("                                    item.querySelector('.fa-check-circle').style.display = 'block';\n");
      out.write("                                } else {\n");
      out.write("                                    item.style.borderColor = 'transparent';\n");
      out.write("                                    item.style.backgroundColor = 'transparent';\n");
      out.write("                                    item.querySelector('.fa-check-circle').style.display = 'none';\n");
      out.write("                                }\n");
      out.write("                            });\n");
      out.write("\n");
